    const scamPatterns = [
      /urgent.*action.*required/i,
      /click.*here.*immediately/i,
      /you.*won.*\$[\d,]+/i,
      /limited.*time.*offer/i,
      /verify.*account.*suspended/i,
      /congratulations.*winner/i,
      /free.*money/i
    ];
    
    scamPatterns.forEach(pattern => {
      if (pattern.test(content)) scamScore += 15;
    });
    
    // URL analysis
    const urls = content.match(/https?:\/\/[^\s]+/g) || [];
    urls.forEach(url => {
      if (url.includes('bit.ly') || url.includes('tinyurl') || url.startsWith('http://')) {
        scamScore += 20;
      }
    });
    
    const credibilityScore = Math.max(100 - scamScore - 10, 0);
    const verdict = credibilityScore > 60 ? 'Credible' : scamScore > 70 ? 'High Risk' : 'Suspicious';
    
    return {
      scamRisk: Math.min(scamScore, 100),
      credibilityScore: credibilityScore,
      verdict: verdict,
      warnings: scamScore > 0 ? ['Potential scam patterns detected', 'Verify through official channels'] : [],
      recommendations: [
