import React, { useState, useEffect } from 'react';

const App = () => {
  console.log('üöÄ Minimal App starting');
  
  const [currentSection, setCurrentSection] = useState('home');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Simple Header */}
      <header className="bg-purple-600 text-white p-4">
        <div className="flex items-center justify-between">
          <h1 className="text-2xl font-bold">üß† VANTA AI</h1>
          {isMobile && (
            <button 
              onClick={toggleSidebar}
              className="text-2xl"
            >
              {sidebarOpen ? '‚úï' : '‚ò∞'}
            </button>
          )}
        </div>
      </header>

      <div className="flex">
        {/* Simple Sidebar */}
        {(sidebarOpen || !isMobile) && (
          <aside className={`${isMobile ? 'fixed inset-0 z-50 bg-white' : 'w-80 bg-white'} p-6 border-r`}>
            {isMobile && (
              <button 
                onClick={() => setSidebarOpen(false)}
                className="float-right text-2xl"
              >
                ‚úï
              </button>
            )}
            <nav className="space-y-4 mt-8">
              {['home', 'verify', 'education', 'community'].map(section => (
                <a
                  key={section}
                  href={`#${section}`}
                  onClick={() => {
                    setCurrentSection(section);
                    setIsMobile && setSidebarOpen(false);
                  }}
                  className="block p-3 rounded hover:bg-purple-100 capitalize"
                >
                  {section}
                </a>
              ))}
            </nav>
          </aside>
        )}

        {/* Main Content */}
        <main className={`flex-1 p-8 ${!isMobile ? 'ml-0' : ''}`}>
          <div className="max-w-4xl mx-auto">
            {currentSection === 'home' && (
              <div className="text-center">
                <div className="bg-purple-600 text-white p-8 rounded-2xl mb-8">
                  <h1 className="text-4xl font-bold mb-4">Welcome to VANTA AI</h1>
                  <p className="text-xl">Advanced AI-powered misinformation detection</p>
                </div>
                <div className="grid md:grid-cols-3 gap-6">
                  <div className="bg-white p-6 rounded-xl shadow">
                    <div className="text-4xl mb-4">üõ°Ô∏è</div>
                    <h3 className="text-xl font-bold">Security</h3>
                    <p>Advanced threat detection</p>
                  </div>
                  <div className="bg-white p-6 rounded-xl shadow">
                    <div className="text-4xl mb-4">üß†</div>
                    <h3 className="text-xl font-bold">AI Processing</h3>
                    <p>Intelligent analysis</p>
                  </div>
                  <div className="bg-white p-6 rounded-xl shadow">
                    <div className="text-4xl mb-4">üìä</div>
                    <h3 className="text-xl font-bold">Analytics</h3>
                    <p>Comprehensive insights</p>
                  </div>
                </div>
              </div>
            )}

            {currentSection === 'verify' && (
              <div className="bg-white p-8 rounded-2xl shadow">
                <h2 className="text-3xl font-bold mb-6">üîç Information Verification</h2>
                <textarea 
                  placeholder="Enter text to verify..."
                  className="w-full p-4 border rounded-lg h-32 mb-4"
                />
                <button className="bg-purple-600 text-white px-6 py-3 rounded-lg font-semibold">
                  Verify Information
                </button>
              </div>
            )}

            {currentSection === 'education' && (
              <div className="bg-white p-8 rounded-2xl shadow">
                <h2 className="text-3xl font-bold mb-6">üéì Education & Training</h2>
                <div className="grid md:grid-cols-2 gap-6">
                  <div className="border rounded-lg p-6">
                    <h3 className="text-xl font-bold mb-2">AI Security Fundamentals</h3>
                    <p className="text-gray-600 mb-4">Learn AI-powered security basics</p>
                    <button className="bg-purple-600 text-white px-4 py-2 rounded">Start Course</button>
                  </div>
                  <div className="border rounded-lg p-6">
                    <h3 className="text-xl font-bold mb-2">Threat Detection</h3>
                    <p className="text-gray-600 mb-4">Advanced detection methods</p>
                    <button className="bg-purple-600 text-white px-4 py-2 rounded">Start Course</button>
                  </div>
                </div>
              </div>
            )}

            {currentSection === 'community' && (
              <div className="bg-white p-8 rounded-2xl shadow">
                <h2 className="text-3xl font-bold mb-6">üë• Community Hub</h2>
                <p className="text-lg text-gray-600 mb-6">Connect with experts and share knowledge</p>
                <div className="space-y-4">
                  <div className="border-l-4 border-purple-600 pl-4 py-2">
                    <h4 className="font-bold">Recent Discussion</h4>
                    <p className="text-gray-600">Best practices for AI security</p>
                  </div>
                  <div className="border-l-4 border-purple-600 pl-4 py-2">
                    <h4 className="font-bold">Expert Insight</h4>
                    <p className="text-gray-600">New detection algorithms</p>
                  </div>
                </div>
              </div>
            )}
          </div>
        </main>
      </div>
    </div>
  );
};

export default App;
