import React, { useState, useEffect } from 'react';
import { GoogleOAuthProvider, useGoogleLogin, googleLogout } from '@react-oauth/google';
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import './index.css';

const GOOGLE_CLIENT_ID = "your-google-client-id-here";

const App = () => {
  // âœ… ALL STATE VARIABLES
  const [currentSection, setCurrentSection] = useState('home');
  const [user, setUser] = useState(null);
  
  // Sidebar & responsive
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  
  // API & settings
  const [openRouterApiKey, setOpenRouterApiKey] = useState(localStorage.getItem('openrouter-api-key') || '');
  const [showApiKey, setShowApiKey] = useState(false);
  const [theme, setTheme] = useState(localStorage.getItem('xist-theme') || 'light');
  const [fontSize, setFontSize] = useState(parseInt(localStorage.getItem('xist-font-size')) || 16);
  const [animations, setAnimations] = useState(localStorage.getItem('xist-animations') !== 'false');
  
  // Chat functionality
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState('');
  const [isChatLoading, setIsChatLoading] = useState(false);
  
  // Verification system
  const [verifyInput, setVerifyInput] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState(null);
  
  // Authority verification
  const [authorityId, setAuthorityId] = useState('');
  const [authorityIdType, setAuthorityIdType] = useState('');
  const [department, setDepartment] = useState('');
  const [badgeNumber, setBadgeNumber] = useState('');
  const [userAuthority, setUserAuthority] = useState(null);
  
  // Alert system
  const [alertTitle, setAlertTitle] = useState('');
  const [alertDescription, setAlertDescription] = useState('');
  const [threatLevel, setThreatLevel] = useState('');
  const [alertCategory, setAlertCategory] = useState('');

  // âœ… RESPONSIVE EFFECT
  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth < 768;
      setIsMobile(mobile);
      if (mobile) {
        setSidebarCollapsed(true);
      }
    };
    
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // âœ… GOOGLE LOGIN SETUP
  const login = useGoogleLogin({
    onSuccess: async (credentialResponse) => {
      try {
        const response = await fetch(`https://www.googleapis.com/oauth2/v1/userinfo?access_token=${credentialResponse.access_token}`, {
          headers: {
            Authorization: `Bearer ${credentialResponse.access_token}`,
            Accept: 'application/json'
          }
        });
        
        const userData = await response.json();
        setUser(userData);
        console.log('Login successful:', userData);
      } catch (error) {
        console.error('Login error:', error);
      }
    },
    onError: (error) => console.log('Login Failed:', error)
  });

  const handleLaunchProtection = () => {
    if (!user) {
      login();
    } else {
      alert(`Welcome back ${user.name}! ðŸš€ All protection features are active.`);
    }
  };

  // âœ… CHAT FUNCTIONALITY
  const sendChatMessage = async (messageOverride = null) => {
    const message = messageOverride || chatInput;
    if (!message.trim() || !user || isChatLoading) return;

    const userMessage = {
      sender: 'user',
      message: message,
      timestamp: new Date().toISOString()
    };
    
    setChatMessages(prev => [...prev, userMessage]);
    setChatInput('');
    setIsChatLoading(true);

    try {
      const apiKey = localStorage.getItem('openrouter-api-key');
      
      const response = await fetch('http://127.0.0.1:5000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: message,
          user_email: user.email,
          user_name: user.name,
          api_key: apiKey
        })
      });

      const data = await response.json();
      
      const botMessage = {
        sender: 'bot',
        message: data.response,
        timestamp: data.timestamp,
        isAI: !!apiKey
      };
      
      setChatMessages(prev => [...prev, botMessage]);
      
    } catch (error) {
      console.error('Chat error:', error);
      
      const errorMessage = {
        sender: 'bot',
        message: `I'm having connection issues, ${user.name}. Please check your API key in Settings or try again later!`,
        timestamp: new Date().toISOString(),
        isAI: false
      };
      setChatMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsChatLoading(false);
      
      setTimeout(() => {
        const chatContainer = document.getElementById('chat-messages');
        if (chatContainer) {
          chatContainer.scrollTop = chatContainer.scrollHeight;
        }
      }, 100);
    }
  };

  // âœ… ANALYSIS FUNCTIONALITY
  const analyzeContent = async () => {
    if (!verifyInput.trim() || !user) return;
    
    setIsAnalyzing(true);
    setAnalysisResult(null);
    
    try {
      const response = await fetch('http://127.0.0.1:5000/api/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content: verifyInput,
          user_email: user.email,
          user_name: user.name
        })
      });
      
      if (!response.ok) {
        throw new Error('Analysis failed');
      }
      
      const result = await response.json();
      setAnalysisResult(result);
    } catch (error) {
      console.error('Analysis error:', error);
      alert('Analysis failed. Please try again.');
    } finally {
      setIsAnalyzing(false);
    }
  };

  // âœ… AUTHORITY VERIFICATION
  const handleGovernmentIdVerification = async () => {
    if (!user || !authorityId.trim() || !authorityIdType) {
      alert('Please provide all required information');
      return;
    }
    
    try {
      const response = await fetch('http://127.0.0.1:5000/api/auth/verify-government-id', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_email: user.email,
          government_id: authorityId,
          id_type: authorityIdType,
          department: department,
          badge_number: badgeNumber
        })
      });
      
      const data = await response.json();
      
      if (response.ok) {
        setUserAuthority({
          verified: true,
          authority_level: data.authority_level,
          verified_at: data.verified_at,
          department: department
        });
        alert('âœ… Government credentials verified successfully!');
      } else {
        alert('âŒ ' + data.error);
      }
    } catch (error) {
      console.error('Verification error:', error);
      alert('âŒ Verification failed. Please try again.');
    }
  };

  // âœ… COLLAPSIBLE NAVIGATION
  const renderNavigation = () => (
    <nav className={`fixed top-0 left-0 h-full bg-white shadow-lg border-r border-gray-200 z-50 transition-all duration-300 ${
      sidebarCollapsed ? 'w-16' : 'w-64'
    }`}>
      <div className="flex flex-col h-full">
        {/* Header with collapse toggle */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          {!sidebarCollapsed && (
            <div className="flex items-center space-x-2">
              <div className="text-2xl">âš¡</div>
              <div className="font-bold text-xl text-gray-800">Xist AI</div>
            </div>
          )}
          
          <button
            onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
            className="p-2 rounded-lg hover:bg-gray-100 transition-colors"
          >
            {sidebarCollapsed ? 'â–¶ï¸' : 'â—€ï¸'}
          </button>
        </div>
        
        {/* Navigation Items */}
        <div className="flex-1 overflow-y-auto py-4">
          <div className="space-y-1">
            {[
              { id: 'home', icon: 'ðŸ ', label: 'Home' },
              { id: 'verify', icon: 'ðŸ”', label: 'Verify' },
              { id: 'chatbot', icon: 'ðŸ¤–', label: 'AI Assistant' },
              { id: 'analytics', icon: 'ðŸ“Š', label: 'Analytics' },
              { id: 'education', icon: 'ðŸ“š', label: 'Education' },
              { id: 'community', icon: 'ðŸ‘¥', label: 'Community' },
              { id: 'protection', icon: 'ðŸ›¡ï¸', label: 'Protection' },
              { id: 'authority', icon: 'âš–ï¸', label: 'Authority' },
              { id: 'settings', icon: 'âš™ï¸', label: 'Settings' }
            ].map((item) => (
              <button
                key={item.id}
                onClick={() => setCurrentSection(item.id)}
                className={`w-full flex items-center px-4 py-3 text-left transition-all duration-200 ${
                  currentSection === item.id
                    ? 'bg-purple-100 text-purple-700 border-r-3 border-purple-700'
                    : 'text-gray-600 hover:bg-gray-100 hover:text-gray-800'
                } ${sidebarCollapsed ? 'justify-center' : 'space-x-3'}`}
                title={sidebarCollapsed ? item.label : ''}
              >
                <span className="text-xl">{item.icon}</span>
                {!sidebarCollapsed && (
                  <span className="font-medium">{item.label}</span>
                )}
              </button>
            ))}
          </div>
        </div>
        
        {/* User Info at Bottom */}
        {user && (
          <div className={`p-4 border-t border-gray-200 ${sidebarCollapsed ? 'flex justify-center' : ''}`}>
            {sidebarCollapsed ? (
              <img src={user.picture} alt="User" className="w-8 h-8 rounded-full" />
            ) : (
              <div className="flex items-center space-x-3">
                <img src={user.picture} alt="User" className="w-10 h-10 rounded-full" />
                <div className="flex-1 min-w-0">
                  <div className="text-sm font-medium text-gray-900 truncate">{user.name}</div>
                  <div className="text-xs text-gray-500 truncate">{user.email}</div>
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </nav>
  );

  // âœ… SECTION RENDERING
  const renderSection = () => {
    switch (currentSection) {
      case 'home':
        return (
          <div className="space-y-8">
            <div className="relative overflow-hidden bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 rounded-2xl p-8 text-white text-center">
              <div className="relative z-10">
                <h1 className="text-4xl md:text-6xl font-bold mb-4">Welcome to Xist AI</h1>
                <p className="text-2xl md:text-3xl opacity-95 mb-2 font-light">Your Guardian Against Misinformation & Scams</p>
                <p className="text-lg opacity-80 mb-8">Next-generation AI platform for comprehensive digital protection</p>
              </div>
            </div>

            <div className="grid md:grid-cols-2 gap-8">
              <button
                onClick={handleLaunchProtection}
                className="group relative p-8 rounded-2xl text-white font-bold text-xl shadow-2xl transform hover:scale-105 transition-all duration-500 bg-gradient-to-br from-purple-600 to-indigo-700 hover:shadow-purple-500/25 cursor-pointer"
              >
                <div className="text-6xl mb-4">{user ? 'ðŸš€' : 'ðŸ”'}</div>
                <div className="text-2xl font-bold mb-2">{user ? 'Launch Protection' : 'Sign In & Launch'}</div>
                <div className="text-sm opacity-80">
                  {user ? 'AI-powered security and threat detection' : 'Click to sign in with Google and launch protection'}
                </div>
              </button>

              <button
                onClick={() => user ? setCurrentSection('verify') : alert('Please sign in first!')}
                disabled={!user}
                className={`group relative p-8 rounded-2xl text-white font-bold text-xl shadow-2xl transform hover:scale-105 transition-all duration-500 ${
                  user 
                    ? 'bg-gradient-to-br from-cyan-500 to-blue-600 hover:shadow-cyan-500/25 cursor-pointer' 
                    : 'bg-gray-600 cursor-not-allowed opacity-60'
                }`}
              >
                <div className="text-6xl mb-4">ðŸ”</div>
                <div className="text-2xl font-bold mb-2">Verify Information</div>
                <div className="text-sm opacity-80">Real-time fact-checking and analysis</div>
              </button>
            </div>

            {user && (
              <div className="flex justify-center">
                <div className="flex items-center space-x-4 px-6 py-3 bg-white/10 backdrop-blur-sm border border-white/20 rounded-2xl text-gray-800">
                  <img src={user.picture} alt="Profile" className="w-12 h-12 rounded-full" />
                  <div>
                    <div className="font-semibold">Welcome back, {user.name}!</div>
                    <div className="text-sm opacity-80">All features unlocked â€¢ {new Date().toLocaleDateString()}</div>
                  </div>
                  <button 
                    onClick={() => {
                      googleLogout();
                      setUser(null);
                    }}
                    className="ml-4 px-3 py-1 bg-red-500/20 hover:bg-red-500/30 rounded-lg text-sm transition-all duration-300"
                  >
                    Sign Out
                  </button>
                </div>
              </div>
            )}
          </div>
        );

      case 'verify':
        return (
          <div className="max-w-4xl mx-auto space-y-8">
            <div className="text-center">
              <div className="text-6xl mb-6">ðŸ”</div>
              <h1 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">AI-Powered Information Verification</h1>
              <p className="text-xl text-gray-600 max-w-2xl mx-auto">Submit text, URLs, or claims for real-time AI analysis</p>
            </div>

            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <div className="space-y-6">
                <div>
                  <label htmlFor="verify-input" className="block text-sm font-medium text-gray-700 mb-2">
                    Enter text, URL, or claim to verify
                  </label>
                  <textarea
                    id="verify-input"
                    value={verifyInput}
                    onChange={(e) => setVerifyInput(e.target.value)}
                    placeholder="Paste the information, URL, or claim you want to verify here..."
                    rows="6"
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-purple-500 resize-none text-base"
                  />
                </div>

                <div className="flex flex-col sm:flex-row gap-4">
                  <button
                    onClick={analyzeContent}
                    disabled={isAnalyzing || !verifyInput.trim() || !user}
                    className={`flex-1 px-6 py-3 rounded-lg font-semibold transition-all duration-200 flex items-center justify-center space-x-2 min-h-[48px] ${
                      (!user) ? 'bg-gray-300 text-gray-500 cursor-not-allowed' :
                      isAnalyzing ? 'bg-purple-400 text-white cursor-wait' :
                      'bg-purple-600 hover:bg-purple-700 text-white hover:scale-105 transform'
                    }`}
                  >
                    {isAnalyzing ? (
                      <>
                        <div className="animate-spin h-5 w-5 border-2 border-white border-t-transparent rounded-full"></div>
                        <span>Analyzing with AI...</span>
                      </>
                    ) : !user ? (
                      <>
                        <span className="text-xl">ðŸ”</span>
                        <span>Sign in to Analyze</span>
                      </>
                    ) : (
                      <>
                        <span className="text-xl">ðŸ¤–</span>
                        <span>Analyze with AI</span>
                      </>
                    )}
                  </button>
                </div>
              </div>
            </div>

            {analysisResult && (
              <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
                <h3 className="text-xl font-semibold text-gray-900 mb-6 flex items-center">
                  <span className="text-2xl mr-3">ðŸ›¡ï¸</span>
                  AI Analysis Results
                </h3>
                
                <div className="grid md:grid-cols-3 gap-6 mb-6">
                  <div className="text-center p-4 bg-gray-50 rounded-lg">
                    <div className={`text-4xl font-bold mb-2 ${
                      analysisResult.credibilityScore > 70 ? 'text-green-600' : 
                      analysisResult.credibilityScore > 40 ? 'text-yellow-600' : 'text-red-600'
                    }`}>
                      {analysisResult.credibilityScore}%
                    </div>
                    <div className="text-sm text-gray-600">Credibility Score</div>
                  </div>

                  <div className="text-center p-4 bg-gray-50 rounded-lg">
                    <div className={`text-4xl font-bold mb-2 ${
                      analysisResult.scamRisk < 30 ? 'text-green-600' : 
                      analysisResult.scamRisk < 70 ? 'text-yellow-600' : 'text-red-600'
                    }`}>
                      {analysisResult.scamRisk}%
                    </div>
                    <div className="text-sm text-gray-600">Scam Risk</div>
                  </div>

                  <div className="text-center p-4 bg-gray-50 rounded-lg">
                    <div className="text-2xl mb-2">
                      {analysisResult.verdict === 'Credible' ? 'âœ…' : 
                       analysisResult.verdict === 'Suspicious' ? 'âš ï¸' : 'âŒ'}
                    </div>
                    <div className={`font-semibold ${
                      analysisResult.verdict === 'Credible' ? 'text-green-600' : 
                      analysisResult.verdict === 'Suspicious' ? 'text-yellow-600' : 'text-red-600'
                    }`}>
                      {analysisResult.verdict}
                    </div>
                  </div>
                </div>

                <div className="p-4 bg-blue-50 rounded-lg">
                  <h4 className="font-semibold text-blue-900 mb-2">ðŸ§  AI Analysis Summary</h4>
                  <p className="text-blue-800 text-sm">{analysisResult.summary}</p>
                </div>
              </div>
            )}
          </div>
        );

      case 'chatbot':
        return (
          <div className="max-w-4xl mx-auto space-y-6">
            <div className="text-center">
              <div className="text-6xl mb-4">ðŸ¤–</div>
              <h1 className="text-3xl md:text-4xl font-bold text-gray-900 mb-2">AI Safety Assistant</h1>
              <p className="text-xl text-gray-600">Chat with Xist AI for personalized cybersecurity guidance</p>
            </div>

            <div className="bg-white rounded-xl shadow-sm border border-gray-200 h-96 flex flex-col">
              <div className="flex-1 p-4 overflow-y-auto space-y-4" id="chat-messages">
                {chatMessages.length === 0 ? (
                  <div className="text-center text-gray-500 mt-20">
                    <div className="text-4xl mb-4">ðŸ’¬</div>
                    <p>Start a conversation with your AI safety assistant!</p>
                  </div>
                ) : (
                  chatMessages.map((msg, index) => (
                    <div key={index} className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
                      <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                        msg.sender === 'user' 
                          ? 'bg-purple-600 text-white' 
                          : 'bg-gray-100 text-gray-800'
                      }`}>
                        <div className="text-sm font-medium mb-1">
                          {msg.sender === 'user' ? user?.name || 'You' : 'Xist AI'}
                        </div>
                        <div className="whitespace-pre-wrap">{msg.message}</div>
                        <div className="text-xs opacity-70 mt-1">
                          {new Date(msg.timestamp).toLocaleTimeString()}
                        </div>
                      </div>
                    </div>
                  ))
                )}
                {isChatLoading && (
                  <div className="flex justify-start">
                    <div className="bg-gray-100 text-gray-800 px-4 py-2 rounded-lg">
                      <div className="flex items-center space-x-2">
                        <div className="animate-spin h-4 w-4 border-2 border-purple-500 border-t-transparent rounded-full"></div>
                        <span>Xist AI is typing...</span>
                      </div>
                    </div>
                  </div>
                )}
              </div>

              <div className="border-t border-gray-200 p-4">
                <div className="flex space-x-4">
                  <input
                    type="text"
                    value={chatInput}
                    onChange={(e) => setChatInput(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && sendChatMessage()}
                    placeholder={user ? "Ask me about digital safety..." : "Sign in to chat"}
                    disabled={!user}
                    className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500"
                  />
                  <button
                    onClick={sendChatMessage}
                    disabled={!user || !chatInput.trim() || isChatLoading}
                    className={`px-6 py-2 rounded-lg font-semibold transition-all duration-200 ${
                      !user || !chatInput.trim() || isChatLoading
                        ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                        : 'bg-purple-600 hover:bg-purple-700 text-white'
                    }`}
                  >
                    Send
                  </button>
                </div>
              </div>
            </div>
          </div>
        );

      case 'settings':
        return (
          <div className="max-w-4xl mx-auto space-y-8">
            <div className="text-center">
              <div className="text-6xl mb-6">âš™ï¸</div>
              <h1 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">Settings & Preferences</h1>
            </div>

            {/* API Configuration */}
            <div className="bg-white rounded-xl shadow-sm border p-6">
              <h3 className="text-xl font-semibold text-gray-900 mb-4">ðŸ”‘ API Configuration</h3>
              
              <div className="space-y-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">OpenRouter API Key</label>
                  <div className="flex space-x-3">
                    <div className="relative flex-1">
                      <input
                        type={showApiKey ? 'text' : 'password'}
                        value={openRouterApiKey}
                        onChange={(e) => setOpenRouterApiKey(e.target.value)}
                        placeholder="sk-or-v1-your-api-key-here"
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 pr-10"
                      />
                      <button
                        onClick={() => setShowApiKey(!showApiKey)}
                        className="absolute right-2 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                      >
                        {showApiKey ? 'ðŸ™ˆ' : 'ðŸ‘ï¸'}
                      </button>
                    </div>
                    
                    <button
                      onClick={() => {
                        localStorage.setItem('openrouter-api-key', openRouterApiKey);
                        alert('âœ… API key saved successfully!');
                      }}
                      disabled={!openRouterApiKey.trim()}
                      className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                        openRouterApiKey.trim()
                          ? 'bg-green-600 hover:bg-green-700 text-white'
                          : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                      }`}
                    >
                      Save Key
                    </button>
                  </div>
                  
                  <div className="mt-3 p-3 bg-blue-50 rounded-lg">
                    <p className="text-sm text-blue-800">
                      <strong>Get your free API key:</strong> Visit <a href="https://openrouter.ai/keys" target="_blank" rel="noopener noreferrer" className="underline">OpenRouter.ai/keys</a>
                    </p>
                  </div>
                  
                  {openRouterApiKey && (
                    <div className="mt-2 flex items-center text-green-600 text-sm">
                      <span className="mr-2">âœ…</span>
                      <span>API key configured - AI chatbot will use real responses</span>
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Account Settings */}
            {user && (
              <div className="bg-white rounded-xl shadow-sm border p-6">
                <h3 className="text-xl font-semibold text-gray-900 mb-4">ðŸ‘¤ Account</h3>
                <div className="flex items-center space-x-4 mb-6">
                  <img src={user.picture} alt="Profile" className="w-16 h-16 rounded-full" />
                  <div>
                    <div className="font-semibold text-gray-900">{user.name}</div>
                    <div className="text-gray-600">{user.email}</div>
                  </div>
                </div>
              </div>
            )}
          </div>
        );

      default:
        return (
          <div className="text-center py-20">
            <div className="text-6xl mb-4">ðŸš§</div>
            <h2 className="text-2xl font-bold text-gray-900 mb-2">Coming Soon</h2>
            <p className="text-gray-600">This feature is under development</p>
          </div>
        );
    }
  };

  // âœ… MAIN RETURN
  return (
    <GoogleOAuthProvider clientId={GOOGLE_CLIENT_ID}>
      <div className="min-h-screen bg-gray-50 transition-all duration-300">
        {renderNavigation()}
        
        {/* Main Content */}
        <main className={`transition-all duration-300 min-h-screen ${
          sidebarCollapsed ? 'ml-16' : 'ml-64'
        }`}>
          <div className="p-6">
            {renderSection()}
          </div>
          
          {/* Footer */}
          <footer className="bg-gray-900 text-white py-12 mt-16">
            <div className="max-w-6xl mx-auto px-6">
              <div className="grid md:grid-cols-4 gap-8">
                <div className="space-y-4">
                  <div className="flex items-center space-x-3">
                    <div className="text-3xl">âš¡</div>
                    <div className="font-bold text-2xl">Xist AI</div>
                  </div>
                  <p className="text-gray-400 text-sm">Advanced AI-powered platform for digital safety.</p>
                </div>
                
                <div>
                  <h4 className="font-semibold mb-4">ðŸ›¡ï¸ Features</h4>
                  <ul className="space-y-2 text-sm text-gray-400">
                    <li>AI Scam Detection</li>
                    <li>Information Verification</li>
                    <li>Digital Safety Assistant</li>
                  </ul>
                </div>
                
                <div>
                  <h4 className="font-semibold mb-4">ðŸ“ž Support</h4>
                  <ul className="space-y-2 text-sm text-gray-400">
                    <li>24/7 AI Assistant</li>
                    <li>Help Center</li>
                    <li>Privacy Policy</li>
                  </ul>
                </div>
                
                <div>
                  <h4 className="font-semibold mb-4">ðŸŒ Connect</h4>
                  <ul className="space-y-2 text-sm text-gray-400">
                    <li>Community Forum</li>
                    <li>Developer API</li>
                    <li>News & Updates</li>
                  </ul>
                </div>
              </div>
              
              <div className="border-t border-gray-800 mt-8 pt-8 text-center">
                <div className="text-sm text-gray-400">
                  Â© 2025 Xist AI Platform. All rights reserved.
                </div>
              </div>
            </div>
          </footer>
        </main>
        
        {/* Mobile Overlay */}
        {isMobile && !sidebarCollapsed && (
          <div 
            className="fixed inset-0 bg-black bg-opacity-50 z-40"
            onClick={() => setSidebarCollapsed(true)}
          />
        )}
      </div>
    </GoogleOAuthProvider>
  );
};

export default App;
