import React, { useState, useEffect } from 'react';

const App = () => {
  const [currentSection, setCurrentSection] = useState('home');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    if (!isMobile) setSidebarOpen(false);
  }, [isMobile]);

  const menuItems = [
    { id: 'home', label: 'Home', icon: 'üè†' },
    { id: 'verify', label: 'Verify Information', icon: 'üõ°Ô∏è' },
    { id: 'education', label: 'Education', icon: 'üéì' },
    { id: 'community', label: 'Community', icon: 'üë•' },
    { id: 'protection', label: 'Protection', icon: 'üîí' },
    { id: 'analytics', label: 'Analytics', icon: 'üìä' },
    { id: 'authority', label: 'Authority', icon: '‚ö°' },
    { id: 'settings', label: 'Settings', icon: '‚öôÔ∏è' }
  ];

  const renderSection = () => {
    switch (currentSection) {
      case 'home':
  return (
    <div className="space-y-8">
      {/* üöÄ NEW INNOVATIVE HERO SECTION */}
      <div className="relative overflow-hidden bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 rounded-2xl p-8 text-white text-center">
        {/* Animated background blobs */}
        <div className="absolute inset-0 opacity-20">
          <div className="absolute top-1/4 left-1/4 w-72 h-72 bg-cyan-400 rounded-full mix-blend-multiply filter blur-xl animate-pulse"></div>
          <div className="absolute top-3/4 right-1/4 w-72 h-72 bg-purple-400 rounded-full mix-blend-multiply filter blur-xl animate-pulse" style={{animationDelay: '2s'}}></div>
          <div className="absolute bottom-1/4 left-1/2 w-72 h-72 bg-pink-400 rounded-full mix-blend-multiply filter blur-xl animate-pulse" style={{animationDelay: '4s'}}></div>
        </div>
        
        {/* Geometric overlay */}
        <div className="absolute inset-0 opacity-10">
          <div className="absolute top-0 left-0 w-full h-full bg-gradient-to-r from-transparent via-cyan-500/10 to-transparent transform -skew-y-12"></div>
        </div>
        
        {/* Content */}
        <div className="relative z-10">
          <div className="text-6xl mb-6 filter drop-shadow-lg"></div>
          <div> 
                <img src="/logo.png" alt="Xist AI Logo" className="mx-auto h-20 w-20 mb-4 rounded-lg" />
              </div>
          <h1 className="text-4xl md:text-6xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r from-white via-cyan-200 to-purple-200">
            Welcome to Xist AI
          </h1>
          <p className="text-2xl md:text-3xl opacity-95 mb-2 font-light text-cyan-100">
            Your Guardian Against Misinformation & Scams
          </p>
          <p className="text-lg opacity-80 mb-8 text-purple-200">
            Next-generation AI platform for comprehensive digital protection
          </p>
          
          {/* Futuristic button */}
          <button className="group relative px-12 py-5 bg-gradient-to-r from-cyan-500 to-purple-600 rounded-2xl font-bold text-lg shadow-2xl hover:shadow-cyan-500/25 transform hover:scale-105 transition-all duration-500 overflow-hidden">
            <span className="absolute inset-0 w-full h-full bg-gradient-to-r from-cyan-400 to-purple-500 opacity-0 group-hover:opacity-20 transition-opacity duration-500"></span>
            <span className="relative flex items-center space-x-2">
              <span>üöÄ</span>
              <span>Launch Protection</span>
            </span>
          </button>
        </div>
      </div>
      {/* END OF NEW HERO SECTION */}

            
            <div className="grid md:grid-cols-3 gap-6">
              {[
                { icon: 'üõ°Ô∏è', title: 'Scam Protection', desc: 'Advanced detection of fraudulent schemes and malicious content' },
{ icon: 'üîç', title: 'Truth Verification', desc: 'AI-powered fact-checking and misinformation identification' },
{ icon: 'üìä', title: 'Real-time Analysis', desc: 'Instant assessment of digital content credibility and safety' }
              ].map((feature, index) => (
                <div key={index} className="bg-white rounded-2xl p-8 shadow-lg hover:shadow-xl border border-gray-100 text-center transition-all duration-300 hover:scale-105 group">
                  <div className="text-5xl mb-6 transform group-hover:scale-110 transition-transform duration-300">{feature.icon}</div>
                  <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
                  <p className="text-gray-600">{feature.desc}</p>
                </div>
              ))}
            </div>
          </div>
        );

      case 'verify':
        return (
          <div className="max-w-4xl mx-auto space-y-8">
            <div className="text-center">
              <div className="text-6xl mb-4">üîç</div>
              <h1 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">Information Verification</h1>
<p className="text-xl text-gray-600 mb-2">Protect yourself from misinformation and scams</p>
<p className="text-lg text-gray-500">AI-powered analysis for digital safety</p>
            </div>
            
            <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-200">
              <textarea
                placeholder="Enter text to verify..."
                rows="6"
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500 resize-none"
              />
              <button className="mt-6 bg-gradient-to-r from-primary-600 to-primary-700 hover:from-primary-700 hover:to-primary-800 text-white px-8 py-4 rounded-xl font-semibold shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-300">
  üîç Verify Information
</button>
            </div>
          </div>
        );

      case 'education':
        return (
          <div className="space-y-8">
            <div className="text-center">
              <div className="text-6xl mb-4">üéì</div>
              <h1 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">Education & Training</h1>
<p className="text-xl text-gray-600">Master the art of detecting scams and misinformation</p>
            </div>
            
            <div className="grid md:grid-cols-2 gap-6">
              {[
                { title: 'AI Security Fundamentals', desc: 'Learn AI-powered security basics', duration: '4 hours' },
                { title: 'Threat Detection Methods', desc: 'Advanced detection techniques', duration: '6 hours' }
              ].map((course, index) => (
                <div key={index} className="bg-white rounded-xl p-6 shadow-sm border border-gray-200">
                  <h3 className="text-xl font-bold mb-2">{course.title}</h3>
                  <p className="text-gray-600 mb-4">{course.desc}</p>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-500">{course.duration}</span>
                    <button className="bg-primary-600 text-white px-4 py-2 rounded">Start Course</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );

      default:
        return (
          <div className="text-center py-16">
            <div className="text-6xl mb-4">üöß</div>
            <h2 className="text-2xl font-bold text-gray-900 mb-4">Coming Soon</h2>
            <p className="text-gray-600">This section is under development</p>
          </div>
        );
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      {/* üöÄ NEW INNOVATIVE HEADER */}
<header className="bg-gradient-to-r from-slate-900 via-purple-900 to-slate-900 shadow-2xl sticky top-0 z-50 border-b border-cyan-500/20">
  <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
    <div className="flex items-center justify-between h-16">
      <div className="flex items-center space-x-4">
        <div className="relative">
          <img 
            src="/logo.png" 
            alt="Xist AI Logo" 
            className="h-10 w-10 hover:scale-110 transition-all duration-300"
           
          />
          <div className="absolute -inset-1 bg-gradient-to-r from-cyan-500 to-purple-600 rounded-xl blur opacity-30 hover:opacity-70 transition duration-300"></div>
        </div>
        <h1 className="text-2xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-cyan-400 to-purple-400 tracking-wide">
          Xist AI
        </h1>
      </div>

      {/* Mobile menu button */}
      {isMobile && (
        <button
          onClick={() => setSidebarOpen(!sidebarOpen)}
          className="relative p-3 rounded-xl bg-gradient-to-r from-cyan-500/20 to-purple-600/20 border border-cyan-500/30 hover:border-cyan-400/50 transition-all duration-300"
        >
          <div className="text-cyan-400 text-2xl">
            {sidebarOpen ? '‚úï' : '‚ò∞'}
          </div>
        </button>
      )}

      {/* Desktop navigation */}
      {!isMobile && (
        <nav className="flex space-x-6">
          {menuItems.slice(0, 4).map((item) => (
            <button
              key={item.id}
              onClick={() => setCurrentSection(item.id)}
              className={`text-white hover:text-cyan-300 px-3 py-2 rounded-md text-sm font-medium transition-all duration-300 ${
                currentSection === item.id ? 'bg-purple-700/50 text-cyan-300' : ''
              }`}
            >
              {item.label}
            </button>
          ))}
        </nav>
      )}
    </div>
  </div>
</header>
{/* END OF NEW HEADER */}



      <div className="flex">
        {/* Mobile Sidebar */}
        {isMobile && sidebarOpen && (
          <>
            <div
              className="fixed inset-0 bg-black bg-opacity-50 z-40"
              onClick={() => setSidebarOpen(false)}
            />
            <aside className="fixed left-0 top-16 h-screen w-80 bg-white shadow-xl z-50 overflow-y-auto">
              <div className="p-6">
                <nav className="space-y-2">
                  {menuItems.map((item) => (
                    <button
                      key={item.id}
                      onClick={() => {
                        setCurrentSection(item.id);
                        setSidebarOpen(false);
                      }}
                      className={`w-full flex items-center space-x-3 px-4 py-3 text-left rounded-lg transition-all duration-200 ${
                        currentSection === item.id
                          ? 'bg-primary-50 text-primary-700'
                          : 'text-gray-700 hover:bg-gray-100'
                      }`}
                    >
                      <span className="text-xl">{item.icon}</span>
                      <span className="font-medium">{item.label}</span>
                    </button>
                  ))}
                </nav>
              </div>
            </aside>
          </>
        )}

        {/* Desktop Sidebar */}
        {!isMobile && (
          <aside className="w-80 bg-white shadow-lg h-screen overflow-y-auto border-r border-gray-200 fixed left-0 top-16">
            <div className="p-6">
              <nav className="space-y-2">
                {menuItems.map((item) => (
                  <button
                    key={item.id}
                    onClick={() => setCurrentSection(item.id)}
                    className={`w-full flex items-center space-x-3 px-4 py-3 text-left rounded-lg transition-all duration-200 ${
                      currentSection === item.id
                        ? 'bg-primary-50 text-primary-700'
                        : 'text-gray-700 hover:bg-gray-100'
                    }`}
                  >
                    <span className="text-xl">{item.icon}</span>
                    <span className="font-medium">{item.label}</span>
                  </button>
                ))}
              </nav>
            </div>
          </aside>
        )}

        {/* Main Content */}
        <main className={`flex-1 ${!isMobile ? 'ml-80' : ''} p-4 sm:p-6 lg:p-8`}>
          <div className="max-w-7xl mx-auto">
            {renderSection()}
          </div>
        </main>
      </div>
    </div>
  );
};

export default App;
